import React, { useState } from "react";
import { TrackType } from "../types/Types";
import ReactModal from "react-modal";
import "../components/Modal.css";

type EditTrackModalProps = {
  isOpen: boolean;
  trackData: TrackType;
  onClose: () => void;
  onEdit: (id: number, track: TrackType) => void;
};

const EditTrackModal: React.FC<EditTrackModalProps> = ({
  isOpen,
  trackData,
  onClose,
  onEdit,
}) => {
  const [title, setTitle] = useState("");
  const [artist, setArtist] = useState("");
  const [genre, setGenre] = useState("");
  const [bpm, setBPM] = useState(0);

  function handleSubmit(event: React.FormEvent) {
    event.preventDefault();
    const updatedTrack = {
      id: trackData.id,
      title: title,
      artist: artist,
      genre: genre,
      bpm: bpm,
    };
    onEdit(trackData.id, updatedTrack);
    onClose();
  }
  return (
    <ReactModal isOpen={isOpen} onRequestClose={onClose}>
      <div>
        <div>
          <h2 className="modal-title">Add Track</h2>
          <form onSubmit={() => handleSubmit}>
            <div className="form-group">
              <label>Title:</label>
              <input
                className="form-control"
                type="text"
                placeholder={trackData.title}
                value={title}
                onChange={(event) => setTitle(event.target.value)}
              />
            </div>
            <div className="form-group">
              <label>Artist:</label>
              <input
                className="form-control"
                type="text"
                value={artist}
                onChange={(event) => setArtist(event.target.value)}
              />
            </div>
            <div className="form-group">
              <label>Genre:</label>
              <input
                className="form-control"
                type="text"
                value={genre}
                onChange={(event) => setGenre(event.target.value)}
              />
            </div>
            <div className="form-group">
              <label>BPM:</label>
              <input
                className="form-control"
                type="number"
                value={bpm}
                onChange={(event) => setBPM(parseInt(event.target.value))}
              />
            </div>
            <div className="modal-buttons">
              <button className="button" type="submit">
                Add
              </button>
              <button className="button" type="button" onClick={onClose}>
                Cancle
              </button>
            </div>
          </form>
        </div>
      </div>
    </ReactModal>
  );
};

export default EditTrackModal;
