import React from "react";
import { useState, useEffect } from "react";
import Track from "./Track";
import { TrackType } from "../types/Types";
import "../components/TrackList.css";
import axios from "axios";

function TrackList() {
  const [tracks, setTracks] = useState([]);

  const newTrack = {
    title: "Test",
    artist: "Test",
    genre: "Test",
    bpm: 100,
  };

  useEffect(() => {
    fetch("http://localhost:8080/tracks")
      .then((response) => response.json())
      .then((data) => setTracks(data))
      .catch((err) => console.log(err));
  }, []);

  function updateTracks() {
    fetch("http://localhost:8080/tracks")
      .then((response) => response.json())
      .then((data) => setTracks(data))
      .catch((err) => console.log(err));
  }

  async function RemoveTitle(id: number) {
    var url = "http://localhost:8080/tracks/" + id.toString();

    await axios
      .delete(url)
      .then((response) => {
        console.log("Track successfully removed!");
      })
      .catch((error) => {
        console.log(error);
      });
    updateTracks();
  }

  async function AddTitle() {
    await axios
      .post("http://localhost:8080/tracks", newTrack)
      .then((response) => {
        console.log("Track successfully added!");
      })
      .catch((error) => {
        console.log(error);
      });
    updateTracks();
  }

  async function EditTrack(id: number, track: TrackType) {
    var url = "http://localhost:8080/tracks/" + id.toString();
    await axios.put(url, track)
    .then((response) => {
        console.log("Track successfully updated!");
    })
    .catch((error) => {
        console.log(error);
    })
    updateTracks();
  }

  return (
    <div>
      <h2>Favorite Tracks</h2>
      <table className="center">
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Artist</th>
            <th>Genre</th>
            <th>BPM</th>
            <th>Remove Track</th>
          </tr>
        </thead>
        <tbody>
          {tracks.map((track: TrackType) => (
            <Track data={track} onUpdate={EditTrack} onDelete={RemoveTitle} />
          ))}
        </tbody>
      </table>
      <button className="buttonAdd" onClick={() => AddTitle()}>
        Add Track
      </button>
    </div>
  );
}

export default TrackList;
